from typing import Optional
from functools import lru_cache
from pydantic_settings import BaseSettings, SettingsConfigDict


class EmbeddingConfig(BaseSettings):
    embedding_provider: Optional[str] = "ollama"
    embedding_model: Optional[str] = "avr/sfr-embedding-mistral"
    embedding_dimensions: Optional[int] = 3072
    embedding_endpoint: Optional[str] = "http://localhost:11434/api/embeddings"
    embedding_api_key: Optional[str] = ""
    embedding_api_version: Optional[str] = None
    embedding_max_tokens: Optional[int] = 8191
    huggingface_tokenizer: Optional[str] = "Salesforce/SFR-Embedding-Mistral"
    model_config = SettingsConfigDict(env_file=".env", extra="allow")


@lru_cache
def get_embedding_config():
    return EmbeddingConfig()
